# Твой метамаск
sender_address = ''

# Контракт WBNB
WBNB = "0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c"

# Сколько мы хотим потратить на покупку (Значение в BNB!)
Bnb_for_spend = 0.005

# Приватный ключ
private_key = ''

# Напишет в тг о выполненной транзакции
traceback_id = ('', 'me')


def Start_Client():
    """Создаем и запускаем клиент ТГ. При первом старте понадобится регистрация"""
    global app
    app = Client("my_account", api_id=api_id, api_hash=api_hash)
    app.start()


def Start_Web3():
    """Подключаемся к бирже"""
    global web3
    web3 = Web3(Web3.HTTPProvider(bsc))
    print(web3.isConnected())


def Get_Balance():
    """Показывает баланс метамаска"""
    balance = web3.eth.get_balance(sender_address)
    humanReadable = web3.fromWei(balance, 'ether')
    print(humanReadable)


def Contract_search(Research_Channel, contract_pattern, Current_Id, limit=1):
    """Собирает сообщения из тг канала и ищет контракт"""
    while 1:
        Last_Message = (app.get_history(Research_Channel, limit))
        Last_Message_From = Last_Message[0]['from_user']['id']
        if Current_Id == 'All_Id':
            Last_Message_Text = str(Last_Message[0]['text'])
        else:
            Last_Message_Text = str(Last_Message[0]['text']) if Current_Id == Last_Message_From else "Не наш канал"
        print(Last_Message_Text)
        Index_v_soobshenii = re.search(contract_pattern, Last_Message_Text)
        try:
            Current_contract = Last_Message_Text[Index_v_soobshenii.start():Index_v_soobshenii.end()]
            break
        except AttributeError:
            print('Контракта нет')
            time.sleep(1)
    return Current_contract


def Token_Buyer(tokenToBuy):
    """Покупает токен по параметрам и найденному контракту"""
    global Hesh
    tokenToBuy = web3.toChecksumAddress(tokenToBuy)
    spend = web3.toChecksumAddress(WBNB)
    contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)
    nonce = web3.eth.get_transaction_count(sender_address)
    start = time.time()
    pancakeswap2_txn = contract.functions.swapExactETHForTokens(
        0,  # поставь 0 или минимальное значение токенов, которое хочешь получить
        [spend, tokenToBuy],
        sender_address,
        (int(time.time()) + 10000)
    ).buildTransaction({
        'from': sender_address,
        'value': web3.toWei(Bnb_for_spend, 'ether'),
        'gas': 250000,
        'gasPrice': web3.toWei('5', 'gwei'),
        'nonce': nonce,
    })
    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=private_key)
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    Hesh = str(web3.toHex(tx_token))
    print(Hesh)


def traceback(traceback_id):
    """Уведомляет о покупке"""
    for ID in traceback_id:
        app.send_message(ID, f'Ура мы что то купили вот хэш - {Hesh}')
        time.sleep(1)


def main():
    """Вся программа в сборе"""
    Start_Client()
    Start_Web3()
    Get_Balance()
    tokenToBuy = Contract_search(Research_Channel, contract_pattern, Current_Id)
    Token_Buyer(tokenToBuy)
    traceback(traceback_id)


main()
